import React, { useEffect, useRef } from 'react';
import './MaintenanceBody.css';

const MaintenanceBody = () => {
  const bodyRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!bodyRef.current) return;
      
      const { clientX, clientY } = e;
      const { width, height, left, top } = bodyRef.current.getBoundingClientRect();
      
      const x = (clientX - left) / width;
      const y = (clientY - top) / height;
      
      const gradientX = Math.round(x * 100);
      const gradientY = Math.round(y * 100);
      
      bodyRef.current.style.background = `radial-gradient(circle at ${gradientX}% ${gradientY}%, rgba(10, 77, 104, 0.8), rgba(5, 68, 94, 0.9), rgba(0, 58, 84, 1))`;
    };

    document.addEventListener('mousemove', handleMouseMove);
    
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div className="maintenance-body" ref={bodyRef}>
      <div className="maintenance-content">
        <h2>نعتذر منكم</h2>
        <h1>الموقع تحت الصيانة</h1>
        <p>نعمل حاليًا على تحسين خدماتنا لنقدم لكم تجربة مصرفية أفضل</p>
        <p className="return-time">سنعود قريبًا</p>
      </div>
    </div>
  );
};

export default MaintenanceBody;
